class LocalExample1(object):

    @staticmethod
    def local_example1():
        """Example 1 library prints argument passed to the library
        initialization.

        Example:

        +-----------------------------+---------------+-----------------+
        | Library                     | LocalExample1 |                 |
        +-----------------------------+---------------+-----------------+
        | LocalExample.Local Example1 |               |                 |
        +-----------------------------+---------------+-----------------+

        Returns:

        example1
        """
        return 'example1'


